    private void jComboBox_ProductDescrActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jComboBox_ProductDescrActionPerformed
       
        if(this.jComboBox_ProductDescr.getSelectedItem().equals("Pickup Van")){
            
           this.jLabel_ProductCode.setText("PVAN37");
           this.jLabel_CostPerDay.setText("25");  
       }
        else if(this.jComboBox_ProductDescr.getSelectedItem().equals("Bus")){
             this.jLabel_ProductCode.setText("BUS775");
           this.jLabel_CostPerDay.setText("40");  
            
        }
        else if(this.jComboBox_ProductDescr.getSelectedItem().equals("Loan moer")){
             this.jLabel_ProductCode.setText("LM1447");
           this.jLabel_CostPerDay.setText("15");  
            
        }
        else if(this.jComboBox_ProductDescr.getSelectedItem().equals("Building Equipment")){
             this.jLabel_ProductCode.setText("BM365");
           this.jLabel_CostPerDay.setText("20");  
            
        }
        
    }//GEN-LAST:event_jComboBox_ProductDescrActionPerformed

