    private void jButton_TotalActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton_TotalActionPerformed
        
        double total,subTot, tax,Set_due_days ,Cost_per_day;
        
        Set_due_days =Double.parseDouble(this.jLabel_SetDueDays.getText());
        Cost_per_day =Double.parseDouble(this.jLabel_CostPerDay.getText());
       
        double Discount =Double.parseDouble(this.jComboBox_discount.getSelectedItem().toString());
        double discount = (Cost_per_day*Set_due_days)*(Discount/100);
        subTot = (Cost_per_day*Set_due_days)-discount;
        tax  = 0.2*(subTot);
        total =subTot +tax;
        
        this.jLabel_Tax.setText(String.format("$%.2f",tax));
        this.jLabel_SubTot.setText(String.format("$%.2f",subTot));
        this.jLabel_Total.setText(String.format("$%.2f",total));
        
        //Receipt Code
        String product_code =this.jLabel_ProductCode.getText();
        String Costperday =this.jLabel_CostPerDay.getText();
        String NOofdays =this.jLabel_PayDueDays.getText();
        
       this.jTextArea_Receipt.append("\tInventory Management System\n\n\n"       
       +"Product Code:\t\t\t"+product_code+"\nNumber of Days:\t\t"+NOofdays 
       +"\nCost Per Day:\t\t\t$"+Costperday+"\nSet Due Day:\t\t\t"+Set_due_days
       +"\nTax:\t\t\t$"+tax+"\nSubTotal:\t\t\t$"+subTot
       +"\n\n"+"Total:\t\t\t$"+total+"\nDate:\t\t\t"+"03/03/20"
       +"\n\n\tPlease keep the receipt for your records.");
        
        
       
    }//GEN-LAST:event_jButton_TotalActionPerformed

